INSERT INTO necs_goods VALUES('481','172','ECS000481','MSI/微星 G41M-P33 Combo 微星G41 775针 支持D2/D3内存 半固态主板','+','197','0','','100','0.000','0.00','335.00','0.00','0','0','1','微星G41M-P33 微星G41小板 INTEL 775 G41 M-ATX(紧凑型)','','<p><img alt=\"\" _ks_lazy_width=\"1\" _ks_lazy_height=\"1\" src=\"http://img03.taobaocdn.com/imgextra/i3/793156814/T2uUBfXo0NXXXXXXXX_!!793156814.jpg\" /></p>\r\n<p><img alt=\"\" _ks_lazy_width=\"1\" _ks_lazy_height=\"1\" src=\"http://img03.taobaocdn.com/imgextra/i3/793156814/T22zS8Xk0XXXXXXXXX_!!793156814.jpg\" /><img alt=\"\" _ks_lazy_width=\"600\" _ks_lazy_height=\"600\" style=\"float: none; margin: 0px; width: 600px\" src=\"http://img04.taobaocdn.com/imgextra/i4/793156814/T2FVtwXolNXXXXXXXX_!!793156814.jpg\" /></p>\r\n<p><img alt=\"\" _ks_lazy_width=\"1\" _ks_lazy_height=\"1\" src=\"http://img04.taobaocdn.com/imgextra/i4/793156814/T2Rlu8Xh4XXXXXXXXX_!!793156814.jpg\" /></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" _ks_lazy_width=\"1\" _ks_lazy_height=\"1\" src=\"http://img02.taobaocdn.com/imgextra/i2/793156814/T2O6iLXXpXXXXXXXXX_!!793156814.jpg\" /></p>\r\n<h2>產品概要</h2>\r\n<p>&nbsp;</p>\r\n<div style=\"min-height: 500px\">\r\n<table width=\"100%\" border=\"0\">\r\n    <tbody>\r\n        <tr>\r\n            <td valign=\"middle\" align=\"center\" width=\"150\" height=\"200\"><img alt=\"featurePic\" _ks_lazy_width=\"1\" _ks_lazy_height=\"1\" src=\"http://tw.msi.com/pic/feature/feature_20101211114159.jpg\" /></td>\r\n            <td valign=\"top\" style=\"padding-bottom: 1em\">\r\n            <h3 style=\"padding-right: 0px; padding-left: 0px; padding-bottom: 10px; padding-top: 10px\">M-Flash</h3>\r\n            <p style=\"text-align: justify\">&bull; 速度快，靈活性高。<br />\r\n            &bull; 當BIOS損毀時可由USB隨身碟載入BIOS檔案進行開機修復作業。<br />\r\n            &bull; 可從BIOS內利用M-Flash功能，從USB隨身碟內載入BIOS更新檔進行更新作業。</p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"middle\" align=\"center\" width=\"150\" height=\"200\"><img alt=\"featurePic\" _ks_lazy_width=\"1\" _ks_lazy_height=\"1\" src=\"http://tw.msi.com/pic/feature/feature_20120723162356.jpg\" /></td>\r\n            <td valign=\"top\" style=\"padding-bottom: 1em\">\r\n            <h3 style=\"padding-right: 0px; padding-left: 0px; padding-bottom: 10px; padding-top: 10px\">Super Charger</h3>\r\n            <p style=\"text-align: justify\">微星 Super Charger 是 Windows 常駐程式，可提供智慧型充電功能。<br />\r\n            <br />\r\n            <strong>特色</strong><br />\r\n            - 支援iPad/iPhone/Smartphone充電技術<br />\r\n            - 自動偵測裝置所需電量，提供最大1.5A 電流輸出<br />\r\n            - 即使在電腦關機狀態亦可充電 (需主機板上有<span style=\"color: #ff0000\">紅色 USB connector</span>上才有支援)</p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"middle\" align=\"center\" width=\"150\" height=\"200\"><img alt=\"featurePic\" _ks_lazy_width=\"1\" _ks_lazy_height=\"1\" src=\"http://tw.msi.com/pic/feature/feature_20110112183808.jpg\" /></td>\r\n            <td valign=\"top\" style=\"padding-bottom: 1em\">\r\n            <h3 style=\"padding-right: 0px; padding-left: 0px; padding-bottom: 10px; padding-top: 10px\">Live Update 5</h3>\r\n            <p style=\"text-align: justify\"><img alt=\"\" _ks_lazy_width=\"200\" _ks_lazy_height=\"147\" style=\"float: right; margin: 5px 10px; width: 200px; height: 147px\" src=\"http://tw.msi.com/pic/image/product_img/other/mainboard/feature/live_200.jpg\" />MSI Live Update&trade; 5 是一套具備強大功能且方便使用的應用程式，可自動檢測、即時更新系統裝置的驅動程式及BIOS，藉此可省下搜尋各種驅動程式及BIOS的時間與降低風險。<br />\r\n            <br />\r\n            <strong>特色</strong><br />\r\n            - 自動偵測 BIOS/驅動程式/顯卡/工具軟體更新<br />\r\n            - 支援微軟 Windows XP/Vista/7 32/64 位元</p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"middle\" align=\"center\" width=\"150\" height=\"200\"><img alt=\"featurePic\" _ks_lazy_width=\"1\" _ks_lazy_height=\"1\" src=\"http://tw.msi.com/pic/feature/feature_20101221111216.jpg\" /></td>\r\n            <td valign=\"top\" style=\"padd