INSERT INTO necs_goods VALUES('2622','524','DT002622','盈通GT610-1024D3 V1 战神版','+','45','0','','100','0.000','0.00','235.00','0.00','0','0','1','','','<p>\r\n<table border=\"0\" cellspacing=\"1\" cellpadding=\"8\" width=\"740\" bgcolor=\"#f2f2f2\" style=\"widows: 2; text-transform: none; text-indent: 0px; border-spacing: 0px; display: table; border-collapse: collapse; font: 12px/20px simsun; white-space: normal; orphans: 2; letter-spacing: normal; color: rgb(0,0,0); word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">\r\n    <tbody>\r\n        <tr>\r\n            <td bgcolor=\"#cccccc\" style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><strong>产品特性：</strong></td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#ffffff\" style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\">\r\n            <p style=\"padding-bottom: 0px; margin: 1.12em 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\">特性：<br />\r\n            支持Microsoft&reg; DirectX&reg; 11<br />\r\n            这款DirectX 11 GPU支持Shader Model 5.0，专为提供超高性能而设计，以满足Tessellation（曲面细分）GPU加速功能的需要，曲面细分是DirectX 11这一全新API中的关键图形特性。&nbsp;<br />\r\n            支持蓝光3D*<br />\r\n            通过HDMI 1.4接口，在所有兼容的3D播放系统上均无缝支持1080p蓝光3D光盘，让用户在家中即可欣赏影院级画质的3D电影。兼容的3D观看系统包含主动快门式眼镜以及被动偏光显示器。&nbsp;<br />\r\n            硬件视频解码加速**<br />\r\n            本技术集高清视频解码加速与后期处理于一身，能够在播放电影和视频时实现前所未有的画面清晰度、流畅的视频、准确的颜色以及精确的图像缩放。&nbsp;<br />\r\n            支持TrueHD和DTS-HD音频比特流<br />\r\n            完全支持TrueHD与DTS-HD高级无损多声道高清音频编解码器，让用户在自家客厅中即可享受到大师录制的天籁之音。&nbsp;<br />\r\n            英伟达&trade; CUDA&trade;（NVIDIA&reg; CUDA&trade;）技术<br />\r\n            英伟达&trade; CUDA（NVIDIA&reg; CUDA）技术可释放GPU（图形处理器）中众多处理器核心的强大动力来加速视频转码、物理效果模拟以及光线追踪等最棘手的处理任务，与传统CPU相比，能够实现惊人的性能提升。&nbsp;<br />\r\n            英伟达&trade; PhysX&reg;（NVIDIA&reg; PhysX&reg;）技术<br />\r\n            这款英伟达&trade;精视&trade;（NVIDIA&reg; GeForce&reg;）GPU完全支持英伟达&trade; PhysX（NVIDIA&reg; PhysX）技术，在游戏中可实现物理效果互动的全新境界，从而能够带来更加生动逼真的体验。&nbsp;<br />\r\n            支持PCI Express 2.0<br />\r\n            本产品专为全新PCI Express 2.0总线架构设计，可为最需带宽支持的游戏与3D应用程序提供最快的数据传输速度，同时兼容现有PCI Express主板，保持了向后的兼容性，提供了最广泛的支持。</p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</p>','images/201306/thumb_img/2622_thumb_G_1370313074348.jpg','images/201306/goods_img/2622_G_1370313074524.jpg','images/201306/source_img/2622_G_1370313074157.jpg','1','','1','1','0','0','1370313074','100','0','0','0','0','0','0','1370313074','0','','0','-1','0','0');
