INSERT INTO necs_goods VALUES('4673','105','0','ECS004673','TP-LINK 多功能宽带路由器 TL-R460+','+','157','59','','98','0.000','180.00','180.00','0.00','0','0','1','','','<div style=\"widows: 2; text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: rgb(64,64,64); word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><b style=\"font-size: 10pt\">产品描述</b><span style=\"font-size: 10pt\">：</span></div>\r\n<p style=\"padding-bottom: 0px; widows: 2; text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; margin: 1.12em 0px; padding-left: 0px; padding-right: 0px; font: 14px/1.4 tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: rgb(64,64,64); word-spacing: 0px; padding-top: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><font style=\"font-size: 10pt\"><br />\r\n<font style=\"font-size: 10pt\">TL-R460+多功能宽带路由器是专为满足中小型企业办公和家庭上网需要而设计的，性能优越、配置简单。此产品除包含所有宽带路由器常见功能外，还支持定时、按需拨号、上网权限管理、病毒自动隔离、IEEE 802.1X、UPnP、花生壳DDNS、系统安全日志等高级功能，同时提供全中文配置界面，用户界面友好，配置简单易用，是您上网的聪明管家。</font></font></p>\r\n<p style=\"padding-bottom: 0px; widows: 2; text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; margin: 1.12em 0px; padding-left: 0px; padding-right: 0px; font: 14px/1.4 tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: rgb(64,64,64); word-spacing: 0px; padding-top: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">&nbsp;</p>\r\n<p style=\"padding-bottom: 0px; widows: 2; text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; margin: 1.12em 0px; padding-left: 0px; padding-right: 0px; font: 14px/1.4 tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: rgb(64,64,64); word-spacing: 0px; padding-top: 0px; -webkit-text-s