INSERT INTO necs_goods VALUES('7391','105','0','ECS007391','水星 MW313R 无线路由器 3天线穿墙型 路由器','+','386','57','','36','0.000','0.00','50.00','0.00','0','0','1','300M','','<p>&nbsp;</p>\r\n<h2 style=\"margin: 7px 0px 5px; padding: 5px 5px 0px; font-size: 16px; font-family: tahoma, arial, 宋体, sans-serif; background-image: none; height: 22px; width: 400px; border-bottom-color: rgb(222, 222, 223); border-bottom-style: solid; border-bottom-width: 1px;\">主要特性</h2>\r\n<ul style=\"margin: 0px; padding: 0px; list-style: none; font-family: tahoma, arial, 宋体, sans-serif; width: 400px; color: rgb(102, 102, 102); line-height: 21.59375px;\">\r\n    <li style=\"margin: 0px; padding: 0px 0px 0px 15px; list-style: none; background-position: 0px 9px; background-repeat: no-repeat no-repeat;\">\r\n    <p align=\"left\" style=\"margin: 0px; padding: 0px; line-height: 1.4;\">符合IEEE 802.11n、IEEE 802.11g/b、IEEE 802.3/3u标准</p>\r\n    </li>\r\n    <li style=\"margin: 0px; padding: 0px 0px 0px 15px; list-style: none; background-position: 0px 9px; background-repeat: no-repeat no-repeat;\">\r\n    <p align=\"left\" style=\"margin: 0px; padding: 0px; line-height: 1.4;\">无线传输速率最高可达300Mbps</p>\r\n    </li>\r\n    <li style=\"margin: 0px; padding: 0px 0px 0px 15px; list-style: none; background-position: 0px 9px; background-repeat: no-repeat no-repeat;\">\r\n    <p align=\"left\" style=\"margin: 0px; padding: 0px; line-height: 1.4;\">1个WAN口，4个LAN口，均支持自动协商和端口自动翻转</p>\r\n    </li>\r\n    <li style=\"margin: 0px; padding: 0px 0px 0px 15px; list-style: none; background-position: 0px 9px; background-repeat: no-repeat no-repeat;\">\r\n    <p align=\"left\" style=\"margin: 0px; padding: 0px; line-height: 1.4;\">支持IP带宽控制，合理分配内网带宽</p>\r\n    </li>\r\n    <li style=\"margin: 0px; padding: 0px 0px 0px 15px; list-style: none; background-position: 0px 9px; background-repeat: no-repeat no-repeat;\">\r\n    <p align=\"left\" style=\"margin: 0px; padding: 0px; line-height: 1.4;\">支持WDS无线桥接，轻松扩展无线网络</p>\r\n    </li>\r\n   